mapper.select:
select id, gmt_modified from car_info
where car_info.status = #{carStatus}
and car_info.model = #{carModel}
and not exists (
  select order_info.car_id from order_info
  where order_info.status = #{orderStatus}
    and ((order_info.start_time <= #{startTime} and order_info.end_time >= #{startTime})
    or (order_info.start_time <= #{endTime} and order_info.end_time >= #{endTime})
    or (order_info.start_time >= #{startTime} and order_info.end_time <= #{endTime}))
    and order_info.car_id = car_info.id
) limit 1;

select distinct(model) from car_info
where car_info.status = #{carStatus}
and not exists (
  select order_info.car_id from order_info
  where order_info.status = #{orderStatus}
    and ((order_info.start_time <= #{startTime} and order_info.end_time >= #{startTime})
    or (order_info.start_time <= #{endTime} and order_info.end_time >= #{endTime})
    or (order_info.start_time >= #{startTime} and order_info.end_time <= #{endTime}))
    and order_info.car_id = car_info.id
);

mapper.update:
update car_info
set model = #{model,jdbcType=VARCHAR},
  status = #{status,jdbcType=SMALLINT},
  gmt_created = #{gmtCreated,jdbcType=TIMESTAMP},
  gmt_modified = #{gmtModified,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=BIGINT}
